using System;
using System.Collections.Generic;
using System.ComponentModel.DataAnnotations;
using System.ComponentModel.DataAnnotations.Schema;
using System.Linq;
using System.Web;

namespace HLSB.Models
{
    public class Product
    {
        public const string DefaultProductPicture = @"";

        public virtual long ProductId { get; set; }

        [Required()]
        [Index("Product_Idx", IsUnique = true)]
        [MaxLength(200)]
        public virtual string ProductName { get; set; }

        [Required()]
        public virtual string ProductDetail { get; set; }

        [Required()]
        public virtual double ProductPrice { get; set; }

        [Required()]
        public virtual byte[] ProductImage { get; set; }

        [NotMapped]
        public string ProductImageString
        {
            get
            {
                return Convert.ToBase64String(ProductImage ?? Convert.FromBase64String(DefaultProductPicture));
            }
            set
            {
                ProductImage = Convert.FromBase64String(!String.IsNullOrEmpty(value) ? value : DefaultProductPicture);

            }
        }
    }
}